mapper.select:
select member_id,member_name
from(
	select rownum rnum, m.*
	from(
		select member_id, member_name
		from tb_member
		order by ${sort} ${direction}
	) m
) where rnum between #{queryStart} and #{queryEnd}

mapper.update:
update tb_member
set member_phone = #{memberPhone}
where member_id = #{memberId}
